// Validate incoming Omi data
const omiData = $input.item.json;

// Check if conversation is discarded
if (omiData.discarded === true) {
  throw new Error('Conversation was discarded by user - skipping');
}

// Check if there's actual content
if (!omiData.transcript_segments || omiData.transcript_segments.length === 0) {
  throw new Error('No transcript segments found - skipping empty conversation');
}

return { json: omiData };

// Extract and transform Omi conversation data for Letta
const omiData = $input.item.json;

// Build transcript from segments
let transcript = '';
if (omiData.transcript_segments && omiData.transcript_segments.length > 0) {
  transcript = omiData.transcript_segments
    .map(seg => `${seg.is_user ? 'User' : 'Speaker ' + seg.speaker_id}: ${seg.text}`)
    .join('\n');
}

// Extract structured data
const structured = omiData.structured || {};
const title = structured.title || 'Untitled Conversation';
const overview = structured.overview || '';
const category = structured.category || 'other';
const emoji = structured.emoji || 'ðŸ’¬';

// Format timestamps
const createdAt = omiData.created_at || new Date().toISOString();
const startedAt = omiData.started_at || createdAt;
const finishedAt = omiData.finished_at || new Date().toISOString();

// Calculate duration in minutes
const duration = omiData.started_at && omiData.finished_at 
  ? Math.round((new Date(omiData.finished_at) - new Date(omiData.started_at)) / 60000)
  : 0;

// Extract action items if present
const actionItems = omiData.structured?.action_items || [];
const actionItemsText = actionItems.length > 0
  ? '\n\nâœ… Action Items:\n' + actionItems.map((item, i) => `${i + 1}. ${item.description}`).join('\n')
  : '';

// Extract photo descriptions if present
const photoDescriptions = omiData.photos && omiData.photos.length > 0
  ? '\n\nðŸ“¸ Photos:\n' + omiData.photos.map((photo, i) => `${i + 1}. ${photo.description}`).join('\n')
  : '';

// Build rich message for Letta with context
const lettaMessage = `${emoji} ${title}

Category: ${category}
Duration: ${duration} minutes
Time: ${new Date(createdAt).toLocaleString()}

ðŸ“ Overview:
${overview}${actionItemsText}${photoDescriptions}

ðŸ’¬ Full Transcript:
${transcript}

---
This is a memory from my Omi wearable device.`;

// Return formatted data for Letta
return {
  json: {
    message: lettaMessage,
    metadata: {
      source: 'omi',
      title: title,
      category: category,
      emoji: emoji,
      duration_minutes: duration,
      created_at: createdAt,
      started_at: startedAt,
      finished_at: finishedAt,
      has_transcript: transcript.length > 0,
      segment_count: omiData.transcript_segments?.length || 0,
      photos_count: omiData.photos?.length || 0,
      action_items_count: actionItems.length,
      discarded: omiData.discarded || false
    }
  }
};
