// Extract and transform Omi conversation data
const omiData = $input.item.json;

// Build transcript from segments
let transcript = '';
if (omiData.transcript_segments && omiData.transcript_segments.length > 0) {
  transcript = omiData.transcript_segments
    .map(seg => `${seg.is_user ? 'User' : 'Speaker ' + seg.speaker_id}: ${seg.text}`)
    .join('\n');
}

// Extract structured data
const structured = omiData.structured || {};
const title = structured.title || 'Untitled Conversation';
const overview = structured.overview || '';
const category = structured.category || 'other';
const emoji = structured.emoji || 'üí¨';

// Format timestamps
const createdAt = omiData.created_at || new Date().toISOString();
const startedAt = omiData.started_at || createdAt;
const finishedAt = omiData.finished_at || new Date().toISOString();

// Calculate duration in minutes
const duration = omiData.started_at && omiData.finished_at 
  ? Math.round((new Date(omiData.finished_at) - new Date(omiData.started_at)) / 60000)
  : 0;

// Build message exactly like Telegram flow does
const message = `${emoji} ${title}\n\nCategory: ${category}\nDuration: ${duration} minutes\nTime: ${new Date(createdAt).toLocaleString()}\n\nüìù Overview:\n${overview}\n\nüí¨ Full Transcript:\n${transcript}\n\n---\nThis is a memory from my Omi wearable device.`;

// Return in same format as Telegram trigger does
return [{
  json: {
    message: {
      text: message
    }
  }
}];
