// Extract and transform Omi conversation data for Letta
const omiData = $input.item.json;

// Skip discarded conversations
if (omiData.discarded === true) {
  return { json: { skip: true, reason: 'discarded' } };
}

// Build transcript from segments
let transcript = '';
if (omiData.transcript_segments && omiData.transcript_segments.length > 0) {
  transcript = omiData.transcript_segments
    .map(seg => `${seg.is_user ? 'User' : 'Speaker ' + seg.speaker_id}: ${seg.text}`)
    .join('\n');
}

// Extract structured data
const structured = omiData.structured || {};
const title = structured.title || 'Untitled Conversation';
const overview = structured.overview || '';
const category = structured.category || 'other';
const emoji = structured.emoji || 'ðŸ’¬';

// Format timestamps
const createdAt = omiData.created_at || new Date().toISOString();
const duration = omiData.started_at && omiData.finished_at 
  ? Math.round((new Date(omiData.finished_at) - new Date(omiData.started_at)) / 60000)
  : 0;

// Build message for Letta
const lettaMessage = `${emoji} ${title}

Category: ${category}
Duration: ${duration} minutes

ðŸ“ Overview:
${overview}

ðŸ’¬ Transcript:
${transcript}`;

// Return formatted data
return {
  json: {
    skip: false,
    message: lettaMessage,
    metadata: {
      source: 'omi',
      title: title,
      category: category,
      emoji: emoji,
      duration_minutes: duration,
      created_at: createdAt
    }
  }
};

// Extract Letta's response and format for Omi
const lettaResponse = $input.item.json;
const metadata = $node['Transform Omi Data'].json.metadata;

// Get Letta's actual response (usually the assistant message)
const assistantMessage = lettaResponse.messages?.find(m => m.role === 'assistant');
const responseText = assistantMessage?.content || 'Memory stored in Letta';

// Create a notification message for Omi user
// Keep it short (20 words max as per Omi plugin spec)
let notification = '';

// Only send notification if Letta has something meaningful to say
if (responseText && responseText.length > 10 && !responseText.includes('Memory stored')) {
  // Truncate to ~20 words
  const words = responseText.split(' ');
  notification = words.slice(0, 20).join(' ');
  if (words.length > 20) notification += '...';
}

return {
  json: {
    message: notification,
    full_response: responseText,
    title: metadata.title,
    category: metadata.category
  }
};
